export class Logger {
  constructor() {
    this.isSilent = process.argv.includes('--silent');
  }

  write(str) {
    if (this.isSilent) {
      return;
    }

    if (str === undefined) {
      process.stdout.write();

      return;
    }

    process.stdout.write(str);
  }

  log(str) {
    if (this.isSilent) {
      return;
    }

    if (str === undefined) {
      console.log();

      return;
    }

    console.log(str);
  }
}

export const logger = new Logger();
