class Processor {
  constructor({ apiClient, logger }) {
    this.apiClient = apiClient;
    this.logger = logger;
  }

  async process({ event }) {
    const { asset_id, records } = event;

    if (!records.length) {
      return;
    }

    this.logger.info(`Processing event. Asset: ${asset_id}. Received ${records.length} records.`);
  }
}

module.exports = Processor;
